mapper.select:
    	select p.name 'legendText', pro.cont 'y'
		from project p join pro_user pro
		where p.id in (select projectId
		from pro_user
		where userId = #{userId}
		and p.id = pro.projectId) and pro.userId = #{userId};

mapper.update:
UPDATE project 
SET name = #{name},owner = #{owner},des = #{des}
WHERE id = #{id}
